'use strict';

var http = require('http');

/**
 * Объект запроса клиента.
 */

var request = module.exports = {
	__proto__: http.IncomingMessage.prototype,
};

/**
 * Запрос пришел из ajax-приложения?
 * @return bool
 */

request.__defineGetter__('ajax', function () {
	return this.headers['x-requested-with'] && this.headers['x-requested-with'] == 'XMLHttpRequest';
});
